import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDocument } from '../contexts/DocumentContext';
import { useAuth } from '../contexts/AuthContext';
import { 
  ArrowLeftIcon,
  SaveIcon,
  ShareIcon,
  ClockIcon,
  UserIcon
} from '@heroicons/react/24/outline';
import Button from './common/Button';
import LoadingSpinner from './common/LoadingSpinner';
import ShareDocumentModal from './ShareDocumentModal';
import { formatDistanceToNow } from 'date-fns';

// Basic TipTap Editor Component
const TipTapEditor = ({ content, onChange, editable = true }) => {
  const [editorContent, setEditorContent] = useState(content);

  useEffect(() => {
    setEditorContent(content);
  }, [content]);

  const handleChange = (e) => {
    const newContent = e.target.value;
    setEditorContent(newContent);
    onChange(newContent);
  };

  if (!editable) {
    return (
      <div className="prose prose-sm sm:prose lg:prose-lg xl:prose-2xl mx-auto max-w-none">
        <div 
          className="min-h-[400px] p-4 border border-gray-200 rounded-lg bg-white"
          dangerouslySetInnerHTML={{ __html: content }}
        />
      </div>
    );
  }

  return (
    <div className="border border-gray-200 rounded-lg bg-white">
      <textarea
        value={editorContent}
        onChange={handleChange}
        className="w-full min-h-[400px] p-4 resize-none border-0 focus:outline-none focus:ring-0"
        placeholder="Start writing your document..."
      />
    </div>
  );
};

const DocumentEditor = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { currentDocument, loading, getDocument, updateDocument } = useDocument();
  const { user } = useAuth();

  const [content, setContent] = useState('');
  const [title, setTitle] = useState('');
  const [isPublic, setIsPublic] = useState(false);
  const [tags, setTags] = useState('');
  const [showShareModal, setShowShareModal] = useState(false);
  const [saving, setSaving] = useState(false);
  const [lastSaved, setLastSaved] = useState(null);

  useEffect(() => {
    if (id) {
      getDocument(id);
    }
  }, [id, getDocument]);

  useEffect(() => {
    if (currentDocument) {
      setContent(currentDocument.content || '');
      setTitle(currentDocument.title || '');
      setIsPublic(currentDocument.isPublic || false);
      setTags(currentDocument.tags ? currentDocument.tags.join(', ') : '');
    }
  }, [currentDocument]);

  const handleSave = async () => {
    if (!currentDocument) return;

    setSaving(true);
    const result = await updateDocument(currentDocument._id, {
      title,
      content,
      isPublic,
      tags: tags ? tags.split(',').map(tag => tag.trim()).filter(tag => tag) : []
    });
    setSaving(false);

    if (result.success) {
      setLastSaved(new Date());
    }
  };

  const canEdit = currentDocument && (
    currentDocument.author._id === user.id ||
    currentDocument.collaborators.some(c => c.user._id === user.id && c.permission === 'edit')
  );

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!currentDocument) {
    return (
      <div className="text-center py-12">
        <h3 className="text-lg font-medium text-gray-900">Document not found</h3>
        <p className="mt-1 text-sm text-gray-500">The document you're looking for doesn't exist.</p>
        <Button onClick={() => navigate('/dashboard')} className="mt-4">
          Back to Dashboard
        </Button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-4">
          <Button
            variant="ghost"
            onClick={() => navigate('/dashboard')}
            className="flex items-center"
          >
            <ArrowLeftIcon className="h-4 w-4 mr-2" />
            Back
          </Button>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">{currentDocument.title}</h1>
            <div className="flex items-center space-x-4 text-sm text-gray-500 mt-1">
              <div className="flex items-center">
                <UserIcon className="h-4 w-4 mr-1" />
                {currentDocument.author.name}
              </div>
              <div className="flex items-center">
                <ClockIcon className="h-4 w-4 mr-1" />
                {formatDistanceToNow(new Date(currentDocument.lastModified), { addSuffix: true })}
              </div>
              {lastSaved && (
                <span className="text-green-600">
                  Saved {formatDistanceToNow(lastSaved, { addSuffix: true })}
                </span>
              )}
            </div>
          </div>
        </div>

        <div className="flex items-center space-x-2">
          {canEdit && (
            <Button
              onClick={handleSave}
              loading={saving}
              disabled={saving}
              className="flex items-center"
            >
              <SaveIcon className="h-4 w-4 mr-2" />
              Save
            </Button>
          )}
          <Button
            variant="outline"
            onClick={() => setShowShareModal(true)}
            className="flex items-center"
          >
            <ShareIcon className="h-4 w-4 mr-2" />
            Share
          </Button>
        </div>
      </div>

      {/* Document Settings (for editors) */}
      {canEdit && (
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h3 className="text-sm font-medium text-gray-900 mb-3">Document Settings</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Title
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Tags
              </label>
              <input
                type="text"
                value={tags}
                onChange={(e) => setTags(e.target.value)}
                placeholder="comma-separated tags"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>
            <div className="flex items-center">
              <input
                id="isPublic"
                type="checkbox"
                checked={isPublic}
                onChange={(e) => setIsPublic(e.target.checked)}
                className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
              />
              <label htmlFor="isPublic" className="ml-2 block text-sm text-gray-900">
                Make public
              </label>
            </div>
          </div>
        </div>
      )}

      {/* Editor */}
      <div className="bg-white rounded-lg border border-gray-200">
        <TipTapEditor
          content={content}
          onChange={setContent}
          editable={canEdit}
        />
      </div>

      {/* Collaborators */}
      {currentDocument.collaborators && currentDocument.collaborators.length > 0 && (
        <div className="bg-white p-4 rounded-lg border border-gray-200">
          <h3 className="text-sm font-medium text-gray-900 mb-3">Collaborators</h3>
          <div className="flex flex-wrap gap-2">
            {currentDocument.collaborators.map((collaborator) => (
              <div
                key={collaborator.user._id}
                className="flex items-center space-x-2 px-3 py-1 bg-gray-100 rounded-full"
              >
                <div className="h-6 w-6 rounded-full bg-primary-600 flex items-center justify-center">
                  <span className="text-xs font-medium text-white">
                    {collaborator.user.name.charAt(0).toUpperCase()}
                  </span>
                </div>
                <span className="text-sm text-gray-700">{collaborator.user.name}</span>
                <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${
                  collaborator.permission === 'edit' 
                    ? 'bg-green-100 text-green-800' 
                    : 'bg-yellow-100 text-yellow-800'
                }`}>
                  {collaborator.permission === 'edit' ? 'Editor' : 'Viewer'}
                </span>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Share Modal */}
      <ShareDocumentModal
        isOpen={showShareModal}
        onClose={() => setShowShareModal(false)}
        document={currentDocument}
      />
    </div>
  );
};

export default DocumentEditor;
