import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import { useDocument } from '../contexts/DocumentContext';
import { useAuth } from '../contexts/AuthContext';
import { 
  PlusIcon, 
  EyeIcon,
  PencilIcon,
  TrashIcon,
  ShareIcon,
  ClockIcon,
  UserIcon,
  DocumentTextIcon
} from '@heroicons/react/24/outline';
import Button from './common/Button';
import LoadingSpinner from './common/LoadingSpinner';
import CreateDocumentModal from './CreateDocumentModal';
import ShareDocumentModal from './ShareDocumentModal';
import { formatDistanceToNow } from 'date-fns';

const Dashboard = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showShareModal, setShowShareModal] = useState(false);
  const [selectedDocument, setSelectedDocument] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  const { documents, loading, pagination, getDocuments, searchDocuments, deleteDocument } = useDocument();
  const { user } = useAuth();
  const navigate = useNavigate();

  const searchQuery = searchParams.get('search');
  const showNewForm = searchParams.get('new') === 'true';

  useEffect(() => {
    if (showNewForm) {
      setShowCreateModal(true);
      setSearchParams({});
    }
  }, [showNewForm, setSearchParams]);

  useEffect(() => {
    if (searchQuery) {
      searchDocuments(searchQuery);
    } else {
      getDocuments(currentPage);
    }
  }, [searchQuery, currentPage, getDocuments, searchDocuments]);

  const handleCreateDocument = () => {
    setShowCreateModal(true);
  };

  const handleEditDocument = (document) => {
    navigate(`/document/${document._id}`);
  };

  const handleViewDocument = (document) => {
    navigate(`/document/${document._id}`);
  };

  const handleShareDocument = (document) => {
    setSelectedDocument(document);
    setShowShareModal(true);
  };

  const handleDeleteDocument = async (document) => {
    if (window.confirm('Are you sure you want to delete this document?')) {
      await deleteDocument(document._id);
    }
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const getPermissionBadge = (document) => {
    if (document.author._id === user.id) {
      return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">Owner</span>;
    }

    const collaborator = document.collaborators.find(c => c.user._id === user.id);
    if (collaborator) {
      return (
        <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${
          collaborator.permission === 'edit' 
            ? 'bg-green-100 text-green-800' 
            : 'bg-yellow-100 text-yellow-800'
        }`}>
          {collaborator.permission === 'edit' ? 'Editor' : 'Viewer'}
        </span>
      );
    }

    return <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-gray-100 text-gray-800">Public</span>;
  };

  if (loading && documents.length === 0) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">
            {searchQuery ? `Search Results for "${searchQuery}"` : 'My Documents'}
          </h1>
          <p className="mt-1 text-sm text-gray-500">
            {searchQuery 
              ? `${documents.length} document(s) found`
              : 'Manage and collaborate on your documents'
            }
          </p>
        </div>
        <Button onClick={handleCreateDocument} className="flex items-center">
          <PlusIcon className="h-4 w-4 mr-2" />
          New Document
        </Button>
      </div>

      {/* Documents Grid */}
      {documents.length === 0 ? (
        <div className="text-center py-12">
          <DocumentTextIcon className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">No documents</h3>
          <p className="mt-1 text-sm text-gray-500">
            {searchQuery 
              ? 'No documents found matching your search.'
              : 'Get started by creating a new document.'
            }
          </p>
          {!searchQuery && (
            <div className="mt-6">
              <Button onClick={handleCreateDocument}>
                <PlusIcon className="h-4 w-4 mr-2" />
                New Document
              </Button>
            </div>
          )}
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {documents.map((document) => (
            <div key={document._id} className="bg-white overflow-hidden shadow rounded-lg">
              <div className="p-6">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium text-gray-900 truncate">
                    {document.title}
                  </h3>
                  {getPermissionBadge(document)}
                </div>

                <div className="mt-2 flex items-center text-sm text-gray-500">
                  <UserIcon className="h-4 w-4 mr-1" />
                  {document.author.name}
                </div>

                <div className="mt-2 flex items-center text-sm text-gray-500">
                  <ClockIcon className="h-4 w-4 mr-1" />
                  {formatDistanceToNow(new Date(document.lastModified), { addSuffix: true })}
                </div>

                {document.tags && document.tags.length > 0 && (
                  <div className="mt-3 flex flex-wrap gap-1">
                    {document.tags.slice(0, 3).map((tag, index) => (
                      <span
                        key={index}
                        className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800"
                      >
                        {tag}
                      </span>
                    ))}
                    {document.tags.length > 3 && (
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-gray-100 text-gray-800">
                        +{document.tags.length - 3}
                      </span>
                    )}
                  </div>
                )}

                <div className="mt-4 flex justify-between">
                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleViewDocument(document)}
                      className="inline-flex items-center px-2.5 py-1.5 border border-gray-300 shadow-sm text-xs font-medium rounded text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                    >
                      <EyeIcon className="h-3 w-3 mr-1" />
                      View
                    </button>
                    {document.author._id === user.id && (
                      <button
                        onClick={() => handleEditDocument(document)}
                        className="inline-flex items-center px-2.5 py-1.5 border border-gray-300 shadow-sm text-xs font-medium rounded text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                      >
                        <PencilIcon className="h-3 w-3 mr-1" />
                        Edit
                      </button>
                    )}
                  </div>

                  <div className="flex space-x-2">
                    <button
                      onClick={() => handleShareDocument(document)}
                      className="inline-flex items-center px-2.5 py-1.5 border border-gray-300 shadow-sm text-xs font-medium rounded text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary-500"
                    >
                      <ShareIcon className="h-3 w-3 mr-1" />
                      Share
                    </button>
                    {document.author._id === user.id && (
                      <button
                        onClick={() => handleDeleteDocument(document)}
                        className="inline-flex items-center px-2.5 py-1.5 border border-red-300 shadow-sm text-xs font-medium rounded text-red-700 bg-white hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                      >
                        <TrashIcon className="h-3 w-3 mr-1" />
                        Delete
                      </button>
                    )}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Pagination */}
      {!searchQuery && pagination.pages > 1 && (
        <div className="flex items-center justify-between">
          <div className="flex-1 flex justify-between sm:hidden">
            <Button
              variant="outline"
              disabled={!pagination.hasPrev}
              onClick={() => handlePageChange(currentPage - 1)}
            >
              Previous
            </Button>
            <Button
              variant="outline"
              disabled={!pagination.hasNext}
              onClick={() => handlePageChange(currentPage + 1)}
            >
              Next
            </Button>
          </div>
          <div className="hidden sm:flex-1 sm:flex sm:items-center sm:justify-between">
            <div>
              <p className="text-sm text-gray-700">
                Showing page <span className="font-medium">{pagination.current}</span> of{' '}
                <span className="font-medium">{pagination.pages}</span>
              </p>
            </div>
            <div>
              <nav className="relative z-0 inline-flex rounded-md shadow-sm -space-x-px">
                <Button
                  variant="outline"
                  size="sm"
                  disabled={!pagination.hasPrev}
                  onClick={() => handlePageChange(currentPage - 1)}
                  className="rounded-l-md"
                >
                  Previous
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  disabled={!pagination.hasNext}
                  onClick={() => handlePageChange(currentPage + 1)}
                  className="rounded-r-md"
                >
                  Next
                </Button>
              </nav>
            </div>
          </div>
        </div>
      )}

      {/* Modals */}
      <CreateDocumentModal
        isOpen={showCreateModal}
        onClose={() => setShowCreateModal(false)}
      />

      <ShareDocumentModal
        isOpen={showShareModal}
        onClose={() => setShowShareModal(false)}
        document={selectedDocument}
      />
    </div>
  );
};

export default Dashboard;
