import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { useDocument } from '../contexts/DocumentContext';
import { useAuth } from '../contexts/AuthContext';
import { 
  PlusIcon, 
  DocumentTextIcon, 
  ClockIcon, 
  UserIcon,
  MagnifyingGlassIcon,
  FunnelIcon,
  ViewColumnsIcon,
  Squares2X2Icon,
  ListBulletIcon,
  StarIcon,
  ShareIcon,
  TrashIcon,
  PencilIcon,
  EyeIcon,
  DocumentPlusIcon,
  FolderIcon,
  SparklesIcon,
  WifiIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';
import Button from './common/Button';
import LoadingSpinner from './common/LoadingSpinner';
import { formatDistanceToNow } from 'date-fns';

const Dashboard = () => {
  const { documents, loading, error, isConnected, getDocuments, deleteDocument, createDocument } = useDocument();
  const { user, logout } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all');
  const [viewMode, setViewMode] = useState('grid');
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newDocument, setNewDocument] = useState({
    title: '',
    content: '',
    isPublic: false,
    tags: ''
  });

  // Memoize getDocuments to prevent unnecessary re-renders
  const fetchDocuments = useCallback(() => {
    if (isConnected) {
      getDocuments();
    }
  }, [getDocuments, isConnected]);

  useEffect(() => {
    fetchDocuments();
  }, [fetchDocuments]);

  const filteredDocuments = documents?.data?.filter(doc => {
    const matchesSearch = doc.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         doc.content.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = filter === 'all' || 
                         (filter === 'public' && doc.isPublic) ||
                         (filter === 'private' && !doc.isPublic);
    return matchesSearch && matchesFilter;
  }) || [];

  const handleCreateDocument = async () => {
    if (!newDocument.title.trim()) {
      alert('Please enter a document title');
      return;
    }

    const result = await createDocument({
      title: newDocument.title,
      content: newDocument.content,
      isPublic: newDocument.isPublic,
      tags: newDocument.tags ? newDocument.tags.split(',').map(tag => tag.trim()).filter(tag => tag) : []
    });

    if (result.success) {
      setShowCreateModal(false);
      setNewDocument({ title: '', content: '', isPublic: false, tags: '' });
    }
  };

  const handleDeleteDocument = async (id) => {
    if (window.confirm('Are you sure you want to delete this document?')) {
      await deleteDocument(id);
    }
  };

  if (loading && !documents?.data?.length) {
    return (
      <div className="flex items-center justify-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50">
      {/* Connection Status Banner */}
      {!isConnected && (
        <div className="bg-red-50 border-b border-red-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-2">
            <div className="flex items-center justify-center text-red-700">
              <ExclamationTriangleIcon className="h-4 w-4 mr-2" />
              <span className="text-sm font-medium">Connection lost. Attempting to reconnect...</span>
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <DocumentTextIcon className="h-8 w-8 text-primary-600" />
                <h1 className="text-2xl font-bold text-gray-900">Document Hub</h1>
              </div>
              <div className="hidden md:flex items-center space-x-1 text-sm text-gray-500">
                <SparklesIcon className="h-4 w-4" />
                <span>Collaborative Platform</span>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2 text-sm text-gray-600">
                <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <UserIcon className="h-4 w-4" />
                <span>{user?.username}</span>
              </div>
              <Button variant="ghost" onClick={logout} className="text-gray-600 hover:text-gray-900">
                Logout
              </Button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <div className="mb-8">
          <div className="bg-gradient-to-r from-primary-600 to-purple-600 rounded-2xl p-8 text-white shadow-xl">
            <div className="flex items-center justify-between">
              <div>
                <h2 className="text-3xl font-bold mb-2">Welcome back, {user?.username}! </h2>
                <p className="text-primary-100 text-lg">
                  Ready to create something amazing? Start a new document or continue where you left off.
                </p>
                <div className="mt-6 flex items-center space-x-4">
                  <Button
                    onClick={() => setShowCreateModal(true)}
                    disabled={!isConnected}
                    className="bg-white text-primary-600 hover:bg-gray-100 shadow-lg transform hover:scale-105 transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <PlusIcon className="h-5 w-5 mr-2" />
                    New Document
                  </Button>
                  <Button 
                    variant="outline" 
                    disabled={!isConnected}
                    className="border-white text-white hover:bg-white hover:text-primary-600 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    <DocumentPlusIcon className="h-5 w-5 mr-2" />
                    Import Document
                  </Button>
                </div>
              </div>
              <div className="hidden lg:block">
                <div className="text-right">
                  <div className="text-4xl font-bold mb-2">{filteredDocuments.length}</div>
                  <div className="text-primary-100">Documents</div>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Statistics Cards */}
        <div className="mb-6 grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="card p-4 animate-fade-in" style={{ '--animation-order': 0 }}>
            <div className="flex items-center">
              <div className="p-2 bg-blue-100 rounded-lg">
                <DocumentTextIcon className="h-6 w-6 text-blue-600" />
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-600">Total Documents</p>
                <p className="text-2xl font-bold text-gray-900">{filteredDocuments.length}</p>
              </div>
            </div>
          </div>

          <div className="card p-4 animate-fade-in" style={{ '--animation-order': 1 }}>
            <div className="flex items-center">
              <div className="p-2 bg-green-100 rounded-lg">
                <EyeIcon className="h-6 w-6 text-green-600" />
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-600">Public</p>
                <p className="text-2xl font-bold text-gray-900">
                  {filteredDocuments.filter(doc => doc.isPublic).length}
                </p>
              </div>
            </div>
          </div>

          <div className="card p-4 animate-fade-in" style={{ '--animation-order': 2 }}>
            <div className="flex items-center">
              <div className="p-2 bg-purple-100 rounded-lg">
                <UserIcon className="h-6 w-6 text-purple-600" />
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-600">Collaborators</p>
                <p className="text-2xl font-bold text-gray-900">
                  {filteredDocuments.reduce((acc, doc) => acc + (doc.collaborators?.length || 0), 0)}
                </p>
              </div>
            </div>
          </div>

          <div className="card p-4 animate-fade-in" style={{ '--animation-order': 3 }}>
            <div className="flex items-center">
              <div className="p-2 bg-yellow-100 rounded-lg">
                <ClockIcon className="h-6 w-6 text-yellow-600" />
              </div>
              <div className="ml-3">
                <p className="text-sm font-medium text-gray-600">Recent</p>
                <p className="text-2xl font-bold text-gray-900">
                  {filteredDocuments.filter(doc => {
                    const docDate = new Date(doc.updatedAt);
                    const weekAgo = new Date();
                    weekAgo.setDate(weekAgo.getDate() - 7);
                    return docDate > weekAgo;
                  }).length}
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* Search and Filters */}
        <div className="mb-6 bg-white rounded-xl shadow-sm border border-gray-200 p-4">
          <div className="flex flex-col sm:flex-row gap-4">
            {/* Search */}
            <div className="flex-1 relative">
              <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search documents..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              />
            </div>

            {/* Filters */}
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <FunnelIcon className="h-5 w-5 text-gray-400" />
                <select
                  value={filter}
                  onChange={(e) => setFilter(e.target.value)}
                  className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
                >
                  <option value="all">All Documents</option>
                  <option value="public">Public</option>
                  <option value="private">Private</option>
                </select>
              </div>

              {/* View Mode Toggle */}
              <div className="flex items-center bg-gray-100 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'grid' ? 'bg-white shadow-sm' : 'text-gray-500 hover:text-gray-700'
                  }`}
                >
                  <Squares2X2Icon className="h-4 w-4" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'list' ? 'bg-white shadow-sm' : 'text-gray-500 hover:text-gray-700'
                  }`}
                >
                  <ListBulletIcon className="h-4 w-4" />
                </button>
              </div>
            </div>
          </div>
        </div>

        {/* Documents Grid/List */}
        {filteredDocuments.length === 0 ? (
          <div className="text-center py-12 animate-fade-in">
            <div className="bg-white rounded-2xl shadow-sm border border-gray-200 p-12">
              <DocumentTextIcon className="h-16 w-16 text-gray-300 mx-auto mb-4 animate-pulse-slow" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No documents found</h3>
              <p className="text-gray-500 mb-6">
                {searchTerm || filter !== 'all' 
                  ? 'Try adjusting your search or filters'
                  : 'Get started by creating your first document'
                }
              </p>
              {!searchTerm && filter === 'all' && (
                <Button
                  onClick={() => setShowCreateModal(true)}
                  className="btn-primary animate-scale-in"
                >
                  <PlusIcon className="h-5 w-5 mr-2" />
                  Create Your First Document
                </Button>
              )}
            </div>
          </div>
        ) : (
          <div className={viewMode === 'grid' ? 'grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6' : 'space-y-4'}>
            {filteredDocuments.map((doc, index) => (
              <div
                key={doc._id}
                className={`document-card ${
                  viewMode === 'list' ? 'flex items-center p-4' : 'p-6'
                }`}
                style={{ '--animation-order': index }}
              >
                {viewMode === 'grid' ? (
                  <>
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex items-center space-x-2">
                        <DocumentTextIcon className="h-6 w-6 text-primary-600" />
                        <div className="flex items-center space-x-1">
                          {doc.isPublic && (
                            <EyeIcon className="h-4 w-4 text-green-500" />
                          )}
                          {doc.tags?.includes('important') && (
                            <StarIcon className="h-4 w-4 text-yellow-500" />
                          )}
                        </div>
                      </div>
                      <div className="flex items-center space-x-1">
                        <button
                          onClick={() => handleDeleteDocument(doc._id)}
                          className="p-1 text-gray-400 hover:text-red-500 transition-colors hover-lift"
                        >
                          <TrashIcon className="h-4 w-4" />
                        </button>
                      </div>
                    </div>

                    <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2">
                      {doc.title}
                    </h3>

                    <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                      {doc.content}
                    </p>

                    <div className="flex items-center justify-between text-xs text-gray-500">
                      <div className="flex items-center">
                        <ClockIcon className="h-3 w-3 mr-1" />
                        {formatDistanceToNow(new Date(doc.lastModified), { addSuffix: true })}
                      </div>
                      <div className="flex items-center">
                        <UserIcon className="h-3 w-3 mr-1" />
                        {doc.author?.name || 'Unknown'}
                      </div>
                    </div>

                    <div className="mt-4 flex items-center space-x-2">
                      <Link
                        to={`/document/${doc._id}`}
                        className="flex-1 btn-primary text-center"
                      >
                        Open
                      </Link>
                      <button className="p-2 text-gray-400 hover:text-primary-600 transition-colors hover-lift">
                        <ShareIcon className="h-4 w-4" />
                      </button>
                    </div>
                  </>
                ) : (
                  <>
                    <div className="flex items-center space-x-4 flex-1">
                      <DocumentTextIcon className="h-8 w-8 text-primary-600" />
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-gray-900">{doc.title}</h3>
                        <p className="text-gray-600 text-sm line-clamp-1">{doc.content}</p>
                      </div>
                    </div>

                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      <div className="flex items-center">
                        <ClockIcon className="h-4 w-4 mr-1" />
                        {formatDistanceToNow(new Date(doc.lastModified), { addSuffix: true })}
                      </div>
                      <div className="flex items-center">
                        <UserIcon className="h-4 w-4 mr-1" />
                        {doc.author?.name || 'Unknown'}
                      </div>
                      <Link
                        to={`/document/${doc._id}`}
                        className="btn-primary"
                      >
                        Open
                      </Link>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Floating Action Button */}
      <div className="fixed bottom-6 right-6 z-40">
        <button
          onClick={() => setShowCreateModal(true)}
          className="bg-primary-600 hover:bg-primary-700 text-white p-4 rounded-full shadow-lg hover:shadow-xl transform hover:scale-110 transition-all duration-200 animate-fade-in"
          style={{ '--animation-order': 4 }}
        >
          <PlusIcon className="h-6 w-6" />
        </button>
      </div>

      {/* Create Document Modal */}
      {showCreateModal && (
        <div className="modal-overlay" onClick={() => setShowCreateModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h2 className="text-xl font-bold text-gray-900 mb-4 gradient-text">Create New Document</h2>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Title
                </label>
                <input
                  type="text"
                  value={newDocument.title}
                  onChange={(e) => setNewDocument({...newDocument, title: e.target.value})}
                  className="input-primary"
                  placeholder="Enter document title"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Content
                </label>
                <textarea
                  value={newDocument.content}
                  onChange={(e) => setNewDocument({...newDocument, content: e.target.value})}
                  className="input-primary"
                  rows="4"
                  placeholder="Start writing your document..."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Tags
                </label>
                <input
                  type="text"
                  value={newDocument.tags}
                  onChange={(e) => setNewDocument({...newDocument, tags: e.target.value})}
                  className="input-primary"
                  placeholder="Enter tags separated by commas"
                />
              </div>

              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="isPublic"
                  checked={newDocument.isPublic}
                  onChange={(e) => setNewDocument({...newDocument, isPublic: e.target.checked})}
                  className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded"
                />
                <label htmlFor="isPublic" className="ml-2 block text-sm text-gray-900">
                  Make document public
                </label>
              </div>
            </div>

            <div className="flex items-center justify-end space-x-3 mt-6">
              <Button
                variant="ghost"
                onClick={() => setShowCreateModal(false)}
                className="btn-secondary"
              >
                Cancel
              </Button>
              <Button
                onClick={handleCreateDocument}
                className="btn-primary"
              >
                Create Document
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
